import React, { useContext } from "react";
import "./CSS/category.css";
import { ShopContext } from "../Context/ShopContext";
import dropdownicon from "../Component/Asset/dropdown_icon.png";
import Item from "../Component/Item/Item";

const ShopCategory = (props) => {
  const { all_product } = useContext(ShopContext);
  const [sortByPrice, setSortByPrice] = useState(false);

  const handleSortByPrice = () => {
    // Toggle trạng thái sắp xếp theo giá
    setSortByPrice(!sortByPrice);
  };

  // Hàm sắp xếp các sản phẩm
  const sortProducts = () => {
    let sortedProducts = [...all_product];

    // Sắp xếp theo giá nếu được chọn
    if (sortByPrice) {
      sortedProducts = sortedProducts.sort((a, b) => {
        const priceA = parseInt(a.new_price.replace(/[^\d]/g, ""));
        const priceB = parseInt(b.new_price.replace(/[^\d]/g, ""));

        // So sánh giá của hai sản phẩm và trả về kết quả phù hợp với logic sắp xếp
        if (priceA < priceB) {
          return -1; // a đứng trước b
        }
        if (priceA > priceB) {
          return 1; // b đứng trước a
        }
        return 0; // giá của a và b bằng nhau
      });
    }

    return sortedProducts;
  };
  return (
    <div className="category">
      {/* <img className="category-banner" src={props.banner} alt="" /> */}
      <div className="sidebar">
        <div>
          <input
            type="checkbox"
            checked={sortByPrice}
            onChange={handleSortByPrice}
          />
          <span>Theo giá từ thấp đến cao</span>
        </div>
        <div>
          <input type="checkbox" name="" id=" " />
          <span>Theo hãng </span>
        </div>
        <input type="checkbox" name="" id=" " />
        <span>Theo chất liệu</span>
      </div>
      <div className="category-product">
        {all_product.map((item, i) => {
          if (props.category === item.category) {
            return (
              <Item
                key={i}
                id={item.id}
                name={item.name}
                image={item.image}
                new_price={item.new_price}
                old_price={item.old_price}
              ></Item>
            );
          } else {
            return null;
          }
        })}
      </div>
    </div>
  );
};

export default ShopCategory;
