import React, { useState } from "react";
import "./CSS/loginSignup.css";

const LoginSignUp = () => {
  const [state, setState] = useState("Đăng nhập");
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    email: "",
  });

  const changeHandler = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const login = async () => {
    console.log("Dang chay ham dang nhap");
  };
  const signup = async () => {
    console.log("Dang chay ham dang ky");
  };
  return (
    <div className="loginSignup">
      <div className="loginSignup-container">
        <h1>{state}</h1>
        <div className="loginSignup-fields">
          {state === "Đăng ký tài khoản" ? (
            <input
              name="username"
              value={formData.username}
              type="text"
              placeholder="Tên của bạn"
            />
          ) : (
            <></>
          )}
          <input type="text" placeholder="Địa chỉ email" />
          <input type="password" name="" id="" placeholder="Mật khẩu" />
          {state === "Đăng ký tài khoản" ? (
            <input
              type="password"
              name=""
              id=""
              placeholder="Nhập lại mật khẩu"
            />
          ) : (
            <></>
          )}
        </div>
        <button
          onClick={() => {
            state === "Đăng nhập" ? login() : signup();
          }}
        >
          Tiếp tục
        </button>
        {state === "Đăng ký tài khoản" ? (
          <p className="loginSignup-login">
            Bạn đã có tài khoản?{" "}
            <span
              onClick={() => {
                setState("Đăng nhập");
              }}
            >
              Đăng nhập tại đây!
            </span>
          </p>
        ) : (
          <></>
        )}
        {state === "Đăng nhập" ? (
          <p className="loginSignup-login">
            Bạn chưa có tài khoản?{" "}
            <span
              onClick={() => {
                setState("Đăng ký tài khoản");
              }}
            >
              Tạo tài khoản tại đây!
            </span>
          </p>
        ) : (
          <></>
        )}
        {state === "Đăng ký tài khoản" ? (
          <div className="loginSignup-agree">
            <input type="checkbox" name="" id="" />
            <p>
              Khi bạn tiếp tục, đồng nghĩa với việc bạn đồng ý các điều khoản sử
              dụng của chúng tôi.
            </p>
          </div>
        ) : (
          <></>
        )}
      </div>
    </div>
  );
};

export default LoginSignUp;
