import React, { useState, useEffect } from "react";
import "./CSS/loginSignup.css";

const LoginSignUp = () => {
  const [state, setState] = useState("Đăng nhập");
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    email: "",
  });
  const [formPassword, setFormPassword] = useState({
    oldPassword: "",
    newPassword: "",
  });

  const [formResetPassword, setFormResetPassword] = useState({
    email: "",
  });
  const [formOTP, setFormOTP] = useState({
    otp: "",
    newPassword: "",
  });

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const token = urlParams.get("token");
    if (token) {
      localStorage.setItem("resetToken", token);
    }
  }, []);

  const changeHandler = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const changePasswordHandler = (e) => {
    setFormPassword({ ...formPassword, [e.target.name]: e.target.value });
  };
  const changeOTP = (e) => {
    setFormPassword({ ...formOTP, [e.target.name]: e.target.value });
  };

  const login = async () => {
    console.log("Dang chay ham dang nhap", formData);
    let responseData;
    await fetch("http://localhost:4000/login", {
      method: "POST",
      headers: {
        Accept: "application/form-data",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => (responseData = data));
    if (responseData.success) {
      localStorage.setItem("auth-token", responseData.token);
      window.location.replace("/");
    } else {
      alert(responseData.errors);
    }
  };
  const signup = async () => {
    console.log("Dang chay ham dang ky", formData);
    let responseData;
    await fetch("http://localhost:4000/signup", {
      method: "POST",
      headers: {
        Accept: "application/form-data",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => (responseData = data));
    if (responseData.success) {
      localStorage.setItem("auth-token", responseData.token);
      window.location.replace("/");
    } else {
      alert(responseData.errors);
    }
  };

  const changePassword = async (_id) => {
    if (!oldPassword || !newPassword) {
      alert("Vui lòng nhập đầy đủ thông tin mật khẩu");
      return;
    }
    const rsp = await fetch(`http://localhost:4000/changepassword/${_id}`, {
      method: "PUT",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ oldPassword, newPassword, _id }),
    });
    const data = await rsp.json();

    // Kiểm tra mật khẩu cũ đúng
    if (!data.success) {
      alert(data.errors);
      return;
    } else {
      alert(data.message);
    }
    await fetchInfor();
  };

  const forgotPassword = async () => {
    const rsp = await fetch("http://localhost:4000/forgotpassword", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ email: formData.email }),
    });
    const data = await rsp.json();
    setState("Nhập mã OTP");
    alert(data.message);
  };

  const resetPassword = async () => {
    const rsp = await fetch("http://localhost:4000/resetpassword", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        otp: formOTP.otp,
        newPassword: formOTP.newPassword,
      }),
    });
    const data = await rsp.json();
    if (data.success) {
      alert(data.message);
      // Sau khi cập nhật mật khẩu, chuyển về trang đăng nhập
      setState("Đăng nhập");
    } else {
      alert(data.errors);
    }
  };
  return (
    <div className="loginSignup">
      <div className="loginSignup-container">
        <h1>{state}</h1>
        <div className="loginSignup-fields">
          {state === "Đăng ký tài khoản" && state !== "Quên mật khẩu" ? (
            <input
              onChange={changeHandler}
              name="username"
              value={formData.username}
              type="text"
              placeholder="Tên của bạn"
            />
          ) : (
            <></>
          )}
          {state !== "Quên mật khẩu" && state !== "Nhập mã OTP" ? (
            <>
              <input
                name="email"
                value={FormData.email}
                onChange={changeHandler}
                type="text"
                placeholder="Địa chỉ email"
              />
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={changeHandler}
                id=""
                placeholder="Mật khẩu"
              />
            </>
          ) : (
            <></>
          )}
          {state === "Quên mật khẩu" ? (
            <>
              <input
                name="email"
                value={formData.email}
                onChange={changeHandler}
                type="text"
                placeholder="Địa chỉ email"
              />
            </>
          ) : (
            <></>
          )}
        </div>
        {state === "Quên mật khẩu" ? (
          <button onClick={forgotPassword}>Gửi mã xác nhận</button>
        ) : (
          <></>
        )}
        {state === "Đăng ký tài khoản" ? (
          <>
            <button
              onClick={() => {
                state === "Đăng nhập" ? login() : signup();
              }}
            >
              Tiếp tục
            </button>
            <p className="loginSignup-login">
              Bạn đã có tài khoản?{" "}
              <span
                onClick={() => {
                  setState("Đăng nhập");
                }}
              >
                Đăng nhập tại đây!
              </span>
            </p>
          </>
        ) : (
          <></>
        )}
        {state === "Đăng nhập" ? (
          <>
            <button
              onClick={() => {
                state === "Đăng nhập" ? login() : signup();
              }}
            >
              Tiếp tục
            </button>
            <p className="loginSignup-login">
              Bạn chưa có tài khoản?{" "}
              <span
                onClick={() => {
                  setState("Đăng ký tài khoản");
                }}
              >
                Tạo tài khoản tại đây!
              </span>
            </p>
          </>
        ) : (
          <></>
        )}
        {state === "Đăng nhập" ? (
          <>
            <p className="loginSignup-login">
              Bạn quên mật khẩu?{" "}
              <span
                onClick={() => {
                  setState("Quên mật khẩu");
                }}
              >
                Đặt mật khẩu mới tại đây
              </span>
            </p>
          </>
        ) : (
          <></>
        )}
        {state === "Đăng ký tài khoản" ? (
          <div className="loginSignup-agree">
            <input type="checkbox" name="" id="" />
            <p>
              Khi bạn tiếp tục, đồng nghĩa với việc bạn đồng ý các điều khoản sử
              dụng của chúng tôi.
            </p>
          </div>
        ) : (
          <></>
        )}
        {state === "Nhập mã OTP" && state !== "Quên mật khẩu" ? (
          <>
            <input
              onChange={changePasswordHandler}
              name="otp"
              value={formPassword.otp}
              type="text"
              placeholder="Mã OTP"
            />
            <input
              onChange={changePasswordHandler}
              name="newPassword"
              value={formPassword.newPassword}
              type="password"
              placeholder="Mật khẩu mới"
            />
            <button onClick={resetPassword}>Đổi mật khẩu</button>
          </>
        ) : (
          <></>
        )}
      </div>
    </div>
  );
};

export default LoginSignUp;
