import React, { createContext, useState } from "react";
import { useEffect } from "react";

export const ShopContext = createContext(null);

const getDefaultCart = () => {
  let cart = {};
  for (let index = 0; index < 300 + 1; index++) {
    cart[index] = 0;
  }
  return cart;
};

const ShopContextProvider = (props) => {
  const [all_product, setAll_Product] = useState([]);
  const [cartItems, setCartItems] = useState(getDefaultCart());

  const fetchInfor = async () => {
    await fetch("http://localhost:4000/allproduct")
      .then((res) => res.json())
      .then((data) => {
        setAll_Product(data);
      });
  };

  useEffect(() => {
    fetchInfor();
  }, []);

  const addToCart = (itemId) => {
    setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] + 1 }));
    // alert("Đã thêm vào giỏ hàng thành công! ^^");
    // console.log(cartItems);
  };
  const removeFromCart = (itemId) => {
    setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] - 1 }));
  };

  const getTotalCart = () => {
    let totalAmount = 0;
    for (const item in cartItems) {
      if (cartItems[item] > 0) {
        let itemInformation = all_product.find(
          (product) => product.id === Number(item)
        );
        let new_price = parseInt(
          itemInformation.new_price.replace(/[^\d]/g, "")
        );
        totalAmount = totalAmount + new_price * cartItems[item];
      }
    }
    let showtotalAmount = totalAmount
      .toString()
      .replace(/\B(?=(\d{3})+(?!\d))/g, ".");
    return showtotalAmount;
  };

  const getTotalItem = () => {
    let totalItem = 0;
    for (const item in cartItems) {
      if (cartItems[item] > 0) {
        totalItem = totalItem + cartItems[item];
      }
    }
    return totalItem;
  };

  const contextValue = {
    getTotalItem,
    getTotalCart,
    all_product,
    cartItems,
    addToCart,
    removeFromCart,
  };

  return (
    <ShopContext.Provider value={contextValue}>
      {props.children}
    </ShopContext.Provider>
  );
};

export default ShopContextProvider;
