import React, { useState } from "react";
import "./CSS/loginSignup.css";

const LoginSignUp = () => {
  const [state, setState] = useState("Đăng ký tài khoản");
  return (
    <div className="loginSignup">
      <div className="loginSignup-container">
        <h1>{state}</h1>
        <div className="loginSignup-fields">
          {state === "Đăng ký tài khoản" ? (
            <input type="text" placeholder="Tên của bạn" />
          ) : (
            <></>
          )}
          <input type="text" placeholder="Địa chỉ email" />
          <input type="password" name="" id="" placeholder="Mật khẩu" />
          <input
            type="password"
            name=""
            id=""
            placeholder="Nhập lại mật khẩu"
          />
        </div>
        <button>Tiếp tục</button>
        <p className="loginSignup-login">
          Bạn đã có tài khoản? <span>Đăng nhập tại đây!</span>
        </p>
        <div className="loginSignup-agree">
          <input type="checkbox" name="" id="" />
          <p>
            Khi bạn tiếp tục, đồng nghĩa với việc bạn đồng ý các điều khoản sử
            dụng của chúng tôi.
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginSignUp;
